import axios from "axios";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

export function AddDrink(){
    const navigate=useNavigate();
    const [drink,setDrink]=useState({id:"",tyoe:"",brand:"",price:""})
    const url = "http://localhost:8088/api/drinks/";
    
    const inputHandler=(e)=>{
        setDrink((drink)=>({
            ...drink,
            [e.target.id]:e.target.value
        }))
    }

    const submitHandler=(e)=>{
        e.preventDefault();
        const data=axios.post(url,drink);
        data.then(resp=>{
          //  if(resp.status===201)
            navigate('/drinklist')
        })
        .catch(error=>console.log(error));
    }

    return(
        <div>
            <h1>Drink Registration</h1>
            <div className="row">
                <div className="col-md-6 offset-3">
                    <form className="form" onSubmit={submitHandler}>
                        <label>Id</label>
                        <input type = "number" id="id" placeholder="Enter Id"
                        value={drink.id} className="form-control" onChange={inputHandler}/>
                        <label>Type</label>
                        <input type = "text" id="type" placeholder="Enter Type"
                        value={drink.type} className="form-control" onChange={inputHandler}/>
                        <label>Brand</label>
                        <input type = "text" id="brand" placeholder="Enter Brand"
                        value={drink.brand} className="form-control" onChange={inputHandler}/>
                        <label>Price</label>
                        <input type = "number" id="price" placeholder="Enter Price"
                        value={drink.price} className="form-control" onChange={inputHandler}/>
                       <br/>
                        <button className="btn btn-primary" type="submit">Register</button>
                    </form>
                </div>
            </div>
   
        </div>
    )

}
