import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export function DrinkList(props){
    const [drinks,setDrinks]= useState([]);
    const url= "http://localhost:8088/api/drinks/";

    const navigate = useNavigate();
    const getData = () =>{
        const data = axios.get(url);
        data.then(resp => setDrinks(resp.data))
        .catch(error => console.log(error));
    }

    const deleteDrink=(id)=>{
        axios.delete(url+id)
        .then(resp=>{console.log(resp); getData()})
        .catch(error=>console.log(error));
    }
    useEffect(()=>{
        getData();
    }, [])

    const tabRow = drinks.map((drink,index)=>{
        return (
            <tr key={index}>
                <td>{drink.id}</td>
                <td>{drink.type}</td>
                <td>{drink.brand}</td>
                <td>{drink.price}</td>
                <td><button className="btn btn-primary"
                    onClick={() => navigate("/viewdrink/" +drink.id)}>View</button>&nbsp;
                    <button className="btn btn-danger" 
                    onClick={()=>deleteDrink(drink.id)}>Delete</button>&nbsp;
                    <button className="btn btn-success"
                    onClick={() => navigate("/editdrink/"+drink.id)}>Edit</button>
                </td>
            </tr>
        )
    })
    return(
        <div>
            <h1>DrinkList Component</h1>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>Id</th><th>Type</th><th>Brand</th><th>Price</th>
                    </tr>
                </thead>
                <tbody>
                    {tabRow}
                </tbody>
            </table>
        </div>
    )
}
