import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export function DoctorList(props){
    const [doctor,setDoctor]= useState([]);
    const url= "http://localhost:8080/api/doc";

    const navigate = useNavigate();
    const getData = () =>{
        const data = axios.get(url);
        data.then(resp => setDoctor(resp.data))
        .catch(error => console.log(error));
    }

    const deleteDoctor=(id)=>{
        axios.delete(url+id)
        .then(resp=>{console.log(resp); getData()})
        .catch(error=>console.log(error));
    }
    useEffect(()=>{
        getData();
    }, [])

    const tabRow = doctor.map((drink,index)=>{
        return (
            <tr key={index}>
                <td>{doctor.id}</td>
                <td>{drink.name}</td>
                <td>{drink.department}</td>
                <td>{drink.slot}</td>
                <td><button className="btn btn-primary"
                    onClick={() => navigate("/viewdoc/" +doctor.id)}>View</button>&nbsp;
                    <button className="btn btn-danger" 
                    onClick={()=>deleteDoctor(doctor.id)}>Delete</button>&nbsp;
                    <button className="btn btn-success"
                    onClick={() => navigate("/editdoc/"+doctor.id)}>Edit</button>
                </td>
            </tr>
        )
    })
    return(
        <div>
            <h1>Doctor List </h1>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th>Id</th><th>Name</th><th>Department</th><th>slot</th>
                    </tr>
                </thead>
                <tbody>
                    {tabRow}
                </tbody>
            </table>
        </div>
    )
}
