import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export function ViewDrink(props){
    const [drink,setDrink]=useState({});
    const param=useParams();
    const navigate=useNavigate();
    const url = "http://localhost:8088/api/drinks/"+param.id;
    const getData = () =>{
        const data = axios.get(url);
        data.then(resp => setDrink(resp.data))
        .catch(error => console.log(error));
    }
    useEffect(() => {
        getData();
    },[])

    return(
        <div>
            <h1>View Drink Component</h1>
            <button className = "btn btn-secondary" onClick={()=>{navigate(-1)}}>Go Back</button>
            <ul className="list-group">
                <li className="list-group-item">Id: {drink.id}</li>
                <li className="list-group-item">Type: {drink.type}</li>
                <li className="list-group-item">Brand: {drink.author}</li>
                <li className="list-group-item">Price: {drink.price}</li>
            </ul>
        </div>
    );
}
